import { useState, useEffect, useNavigate } from 'react';
import './App.css';
import { Autocomplete, TextField, Paper, Box, CircularProgress } from "@mui/material";


function App() {
  //const navigate = useNavigate();
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true)
  useEffect(() => {
    fetch('http://localhost:8081/movies')
      .then((res) => res.json())
      .then((data) => {
        setMovies(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <CircularProgress />; // or your custom loading component
  }

  return (
    <div className="App">
      <Box className="boxes-container" sx={{display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: 2, marginBottom: 6 }}>
        {movies.map((movie, index) => (
          <Box component={Paper} sx={{width: 200, height: 100, margin: 2 }} key={index} elevation={3}>
            <p>{movie.title}</p>
          </Box>
        ))}
      </Box>
      <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={movies.map((option) => option.title)}
        className="autocomplete"
        sx={{width:300}}
        renderInput={(params) => <TextField {...params} label="Movie" variant="outlined" />}
        onInputChange={(event, newInputValue) => {
          setMovies(
            movies.filter((movie) =>
              movie.title.toLowerCase().includes(newInputValue.toLowerCase())
            )
          );
        }}
      />
    </div>
  );
}

export default App;
