import { useState } from 'react';
import './App.css';
import { Autocomplete, TextField, Paper, Box} from "@mui/material";

const allMovies = [
  { title: 'Mean Girls' },
  { title: 'Hackers' },
  { title: 'The Grey' },
  { title: 'Sunshine' },
  { title: 'Ex Machina' },
];

function App() {
  const [movies, setMovies] = useState(allMovies);

  const filterOptions = (options, state) => {
    return options.filter((option) =>
      option.toLowerCase().includes(state.inputValue.toLowerCase())
    );
  };

  return (
    <div className="App">
      {movies.map((movie, index) => (
        <Box component={Paper} padding={2} margin={1} bgcolor="grey.300" key={index} elevation={3}>
        <p>{movie.title}</p>
        </Box>
      ))}
      <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={movies.map((option) => option.title)}
        sx={{width:300}}
        renderInput={(params) =>
          <TextField {...params} label="Movie" variant="outlined" />}
        onInputChange={(event, newInputValue) => {
          setMovies(
            allMovies.filter((movie) =>
              movie.title.toLowerCase().includes(newInputValue.toLowerCase())
            )
          );
        }}
      />
    </div>
  );
}

export default App;
